import { TaskColorHistoryItem } from "../../../Model/History/HistoryItems/Task/Properties/TaskColorHistoryItem";
import { TaskPropertyCommandBase } from "./TaskPropertyCommandBase";

export class TaskColorCommand extends TaskPropertyCommandBase {
    public execute(id: string, value: string): boolean {
        return super.execute(id, value);
    }
    protected executeInternal(id: string, value: string): boolean {
        return this.modelManipulator.dispatcher.raiseTaskColorUpdating(
            this.getTask(id),
            value,
            (newValue) => { this.history.addAndRedo(new TaskColorHistoryItem(this.modelManipulator, id, newValue)); }
        );
    }
}
