import { TaskDescriptionHistoryItem } from "../../../Model/History/HistoryItems/Task/Properties/TaskDescriptionHistoryItem";
import { TaskPropertyCommandBase } from "./TaskPropertyCommandBase";

export class TaskDescriptionCommand extends TaskPropertyCommandBase {
    public execute(id: string, value: string): boolean {
        return super.execute(id, value);
    }
    protected executeInternal(id: string, value: string): boolean {
        return this.modelManipulator.dispatcher.raiseTaskDescriptionUpdating(
            this.getTask(id),
            value,
            (newDescription) => { this.history.addAndRedo(new TaskDescriptionHistoryItem(this.modelManipulator, id, newDescription)); }
        );
    }
}
