import { Task } from "../../../Model/Entities/Task";
import { CommandBase } from "../../CommandBase";
import { SimpleCommandState } from "../../SimpleCommandState";

export class TaskPropertyCommandBase extends CommandBase<SimpleCommandState> {
    public getState(): SimpleCommandState {
        return new SimpleCommandState(this.isEnabled());
    }
    isEnabled(): boolean {
        return super.isEnabled() && this.control.settings.editing.allowTaskUpdate;
    }
    getTask(id: string): Task {
        return this.control.viewModel.tasks.getItemById(id);
    }
}
