import { TaskTitleHistoryItem } from "../../../Model/History/HistoryItems/Task/Properties/TaskTitleHistoryItem";
import { TaskPropertyCommandBase } from "./TaskPropertyCommandBase";

export class TaskTitleCommand extends TaskPropertyCommandBase {
    public execute(id: string, value: string): boolean {
        return super.execute(id, value);
    }
    protected executeInternal(id: string, value: string): boolean {
        return this.modelManipulator.dispatcher.raiseTaskTitleUpdating(
            this.getTask(id),
            value,
            (newTitle) => { this.history.addAndRedo(new TaskTitleHistoryItem(this.modelManipulator, id, newTitle)); }
        );
    }
}
