import { Task } from "../../../Entities/Task";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskColorManipulator extends TaskPropertyManipulator<string> {
    getPropertyValue(task: Task): string {
        return task.color;
    }
    setPropertyValue(task: Task, value: string): void {
        task.color = value;
        this.dispatcher.notifyTaskColorChanged(task.id, value, this.getErrorCallback());
    }
}
