import { Task } from "../../../Entities/Task";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskDescriptionManipulator extends TaskPropertyManipulator<string> {
    getPropertyValue(task: Task): string {
        return task.description;
    }
    setPropertyValue(task: Task, value: string): void {
        task.description = value;
        this.dispatcher.notifyTaskDescriptionChanged(task.id, value, this.getErrorCallback());
    }
}
