import { Task } from "../../../Entities/Task";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskEndDateManipulator extends TaskPropertyManipulator<Date> {
    getPropertyValue(task: Task): Date {
        return task.end;
    }
    setPropertyValue(task: Task, value: Date): void {
        task.end = value;
        this.dispatcher.notifyTaskEndChanged(task.id, value, this.getErrorCallback());
    }
}
