import { Task } from "../../../Entities/Task";
import { DateRange } from "../../../WorkingTime/DateRange";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskMoveManipulator extends TaskPropertyManipulator<DateRange> {
    getPropertyValue(task: Task): DateRange {
        return new DateRange(task.start, task.end);
    }
    setPropertyValue(task: Task, value: DateRange): void {
        task.start = value.start;
        task.end = value.end;
        this.dispatcher.notifyTaskStartChanged(task.id, value.start, this.getErrorCallback());
        this.dispatcher.notifyTaskEndChanged(task.id, value.end, this.getErrorCallback());
    }
}
