import { Task } from "../../../Entities/Task";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskProgressManipulator extends TaskPropertyManipulator<number> {
    getPropertyValue(task: Task): number {
        return task.progress;
    }
    setPropertyValue(task: Task, value: number): void {
        value = value < 0 ? 0 : value > 100 ? 100 : value;
        task.progress = value;
        this.dispatcher.notifyTaskProgressChanged(task.id, value, this.getErrorCallback());
    }
}
