import { Task } from "../../../Entities/Task";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskStartDateManipulator extends TaskPropertyManipulator<Date> {
    getPropertyValue(task: Task): Date {
        return task.start;
    }
    setPropertyValue(task: Task, value: Date): void {
        task.start = value;
        this.dispatcher.notifyTaskStartChanged(task.id, value, this.getErrorCallback());
    }
}
