import { Task } from "../../../Entities/Task";
import { TaskPropertyManipulator } from "./TaskPropertyManipulator";

export class TaskTitleManipulator extends TaskPropertyManipulator<string> {
    getPropertyValue(task: Task): string {
        return task.title;
    }
    setPropertyValue(task: Task, value: string): void {
        task.title = value;
        this.dispatcher.notifyTaskTitleChanged(task.id, value, this.getErrorCallback());
    }
}
